DROP TABLE IF EXISTS `admin`;
CREATE TABLE `admin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `role_id` int(11) NOT NULL DEFAULT '0' COMMENT '所属角色id',
  `username` varchar(32) NOT NULL DEFAULT '' COMMENT '用户名',
  `password` char(64) NOT NULL DEFAULT '' COMMENT '密码',
  `client_ip` varchar(15) NOT NULL DEFAULT '' COMMENT '登录IP',
  `last_login` int(11) NOT NULL DEFAULT '0' COMMENT '登陆时间',
  `status` enum('normal','disable') NOT NULL DEFAULT 'normal' COMMENT '状态',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uniq_username` (`username`)
) ENGINE=InnoDB AUTO_INCREMENT=129 DEFAULT CHARSET=utf8 COMMENT='管理员表';

