-- ----------------------------
-- Table structure for `article`
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '文章ID',
  `custom_url` varchar(50) NOT NULL DEFAULT '' COMMENT '自定义URL',
  `cid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '分类ID',
  `title` varchar(50) NOT NULL DEFAULT '' COMMENT '文章标题',
  `pic` varchar(100) NOT NULL DEFAULT '' COMMENT '文章头图',
  `author` varchar(30) NOT NULL DEFAULT '' COMMENT '作者',
  `brief` varchar(255) NOT NULL DEFAULT '' COMMENT '简要',
  `content` text NOT NULL COMMENT '文章内容',
  `add_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `edit_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '最后编辑时间',
  `orderby` tinyint(4) unsigned NOT NULL DEFAULT '10' COMMENT '排序，数字越小排位越靠前',
  `featured` tinyint(4) NOT NULL DEFAULT '0' COMMENT '推荐',
  `status` enum('close','open') NOT NULL DEFAULT 'close' COMMENT '状态',
  PRIMARY KEY (`id`),
  KEY `orderby` (`orderby`)
) ENGINE=InnoDB AUTO_INCREMENT=400 DEFAULT CHARSET=utf8 COMMENT='文章列表';

-- ----------------------------
-- Records of article
